mapper.select:
SELECT
    coupon_id,
    count( coupon_id )
FROM
    unimall_user_coupon
WHERE
    coupon_id IN #{couponIds}
    AND user_id = #{userId}
GROUP BY
    coupon_id;

SELECT c.id AS id, c.title AS title, c.`type` AS `type`, c.description AS description, c.total AS total, c.surplus AS surplus, c.`limit` AS `limit`, c.discount AS discount, c.min AS min, c.status AS status, g.title as categoryTitle, c.days AS days, c.gmt_start as gmtStart, c.gmt_end as gmtEnd FROM unimall_coupon c LEFT JOIN unimall_category g on c.category_id = g.id WHERE 1 = 1
AND c.title like concat("%",#{title},"%")
AND c.title = #{type}
AND c.status = #{status} AND c.gmt_end > #{now}
AND c.gmt_end < #{now}
limit #{offset},#{limit}
